<?php

namespace ihate\CoreBundle\Repository;

use Doctrine\ORM\EntityRepository;
use ihate\CoreBundle\Entity\User;
use ihate\CoreBundle\Entity\Post;

/**
 * PostRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostRepository extends EntityRepository
{
    public function showPosts(User $user)
    {
        $ids = $this->getFollowersIds($user->getFollowers());
        return $this->createQueryBuilder('p')
            ->addSelect('u, h, hu')
            ->join('p.user', 'u')
            ->leftJoin('p.hates', 'h')
            ->leftJoin('h.user', 'hu')
            ->where('p.user IN (:follow)')
            ->orWhere('p.user = :user')
            ->orWhere('h.user = :user')
            ->orWhere('h.user IN (:follow)')
            ->setParameter('user', $user)
            ->setParameter('follow', $ids)
            ->orderBy('p.createdAt', 'DESC')
            ->getQuery()
            ->getResult();
    }

    private function getFollowersIds($users)
    {
        $ids = array();
        foreach ($users as $user) {
            $ids[] = $user->getId();
        }
        return $ids;
    }

    public function showMyPosts(User $user)
    {
        return $this->createQueryBuilder('p')
            ->where('p.user = :user')
            ->setParameter('user', $user)
            ->orderBy('p.createdAt', 'DESC')
            ->getQuery()
            ->getResult();
    }

    /**
     * @return array
     */
    public function getTop(User $user)
    {
        $country = $user->getCountry();
        $resultS = $this->createQueryBuilder('p')
            ->addSelect('COUNT(h) AS hates')
            ->join('p.hates', 'h')
            ->leftjoin('h.user','u')
            ->where('u.country = :country')
            ->setParameter('country', $country)
            ->groupBy('p')
            ->orderBy('hates', 'DESC')
            ->addOrderBy('p.createdAt', 'DESC')
            ->getQuery()
            ->getResult();

        $response = array();

        foreach ($resultS as $index => $result) {
            $response[] = $result[0];

            if ($index > 3) {
                break;
            }
        }

        return $response;
    }
    /**
     * @return array
     */
    public function getTopAll()
    {
        $resultS = $this->createQueryBuilder('p')
            ->addSelect('COUNT(h) AS hates')
            ->join('p.hates', 'h')
            ->groupBy('p')
            ->orderBy('hates', 'DESC')
            ->addOrderBy('p.createdAt', 'DESC')
            ->getQuery()
            ->getResult();

        $response = array();

        foreach ($resultS as $index => $result) {
            $response[] = $result[0];

            if ($index > 4) {
                break;
            }
        }

        return $response;
    }
}
