<?php

namespace ihate\CoreBundle\Repository;

use Doctrine\ORM\EntityRepository;
use ihate\CoreBundle\Entity\User;
use Doctrine\ORM\Tools\Pagination\Paginator;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserRepository extends EntityRepository
{
    /**
     * @param $needle
     * @param \ihate\CoreBundle\Entity\User $user
     * @return array
     */
    public function search($needle, User $user)
    {
        return $this->createQueryBuilder('u')
            ->where('u.name LIKE :needle')
            ->andWhere('u.id != :id')
            ->setParameters(array(
                'needle' => '%'.$needle.'%',
                'id' => $user->getId(),
            ))
            ->getQuery()
            ->getResult();
    }

    public function getListPaginator($first, $max)
    {
        $qb = $this->createQueryBuilder('p')
            ->orderBy('p.id', 'DESC')
            ->setFirstResult($first)
            ->setMaxResults($max);

        return new Paginator($qb->getQuery());
    }
}
